VertexShader.DefCode:

	varying vec2 texCoord;
	
	void main( void )
	{
		texCoord = gl_MultiTexCoord0.st; 
		gl_Position = gl_ProjectionMatrix * gl_Vertex;
	}

FragmentShader.DefCode:

	uniform sampler2D tex0;
	varying vec2 texCoord;
	
	void main( void )
	{
		vec4 albedo = texture2D( tex0, texCoord );
		
		gl_FragColor = albedo;
	}
